let utopian = require('./api');

setInterval(() => {
    Promise.all([
        utopian.getPendingPostsByCategory('development',{limit:1}),
        utopian.getPendingPostsByCategory('bug-hunting',{limit:1}),
        utopian.getPendingPostsByCategory('documentation',{limit:1}),
        utopian.getPendingPostsByCategory('translations',{limit:1}),
        utopian.getPendingPosts({limit:0})
    ]).then((data) => {
        console.log(data)
    })
}, 2000)